@Mod(modid = RubyCraftMod.MODID, name = RubyCraftMod.NAME, version = RubyCraftMod.VERSION, acceptedMinecraftVersions = "1.7.10", dependencies = "required-after:TBSSCore@[1.0.0,);required-after:Forge@[10.13.4.1448,)")
public class RubyCraftMod
{
    public static final String MODID = "RubyCraftMod";
    public static final String NAME = "RubyCraftMod";
    public static final String VERSION = "1.0.0";
        public static Item Ruby;
        public static Block RubyBlock;
        public static Block RubyOre;
        public static ItemFood RubyApple;

    @EventHandler
    public void preLoad(FMLPreInitializationEvent event)
    {
        RubyBlock=new RubyBlock(Material.rock);
                RubyOre=new RubyOre(Material.rock);
                Ruby=new Item();
                GameRegistry.registerBlock(RubyBlock, RubyBlock.getUnlocalizedName());
                GameRegistry.registerBlock(RubyOre, RubyOre.getUnlocalizedName());
                GameRegistry.registerItem(Ruby, Ruby.getUnlocalizedName());
                GameRegistry.registerItem(RubyApple, RubyApple.getUnlocalizedName());
                GameRegistry.registerWorldGenerator(new OreGen(),0);
                GameRegistry.addRecipe( new ItemStack(RubyApple) ,new
                Object[]{"AAA","A*A","AAA,'A',Ruby,'*',Items.apple"});
                ItemStack smeltStack = new ItemStack(RubyOre);
                GameRegistry.addSmelting(Ruby, smeltStack, 1.0f);
        }

    @EventHandler
    public void init(FMLInitializationEvent event)
    {
        // TODO
    }

    @EventHandler
    public void postInit(FMLPostInitializationEvent event)
    {
        // TODO
    }
