package com.jonjs;

import org.bukkit.ChatColor;
import org.bukkit.command.Command;
import org.bukkit.command.CommandSender;
import org.bukkit.configuration.file.FileConfiguration;
import org.bukkit.entity.Player;
import org.bukkit.plugin.java.JavaPlugin;



public class Main extends JavaPlugin {
        
        
        FileConfiguration config = getConfig();
        
        
    @Override
    public void onEnable() {
            getLogger().info(ChatColor.AQUA + "[NumberCode] 插件已被加载！");
        config.options().copyDefaults(true);
        saveConfig();
        saveDefaultConfig();
    }

    @Override
    public void onDisable() {
        getLogger().info(ChatColor.AQUA + "[NumberCode] 插件已被关闭！");
    }
    
    @Override
    public boolean onCommand(CommandSender sender, Command cmd, String label, String[] args)
    {
            Player p = (Player)sender;
        if(cmd.getName().equalsIgnoreCase("nc"))   
        {          
                if(args[0].equalsIgnoreCase("create"))
                {
                                if(!sender.hasPermission("numbercode.create"))
                                {
                                        sender.sendMessage(getConfig().getString("NoPermissionMessage.create").replace("&", "§"));
                                        return true;
                                }
                  
                  
                  String strminL = args[1];
                  long minL = Long.valueOf(strminL).longValue();
                  String strmaxL = args[2];
                  long maxL = Long.valueOf(strmaxL).longValue();
                  String opc = args[3];
                  String tell = args[4];
                  String stramount = args[6];
                  int amount = Integer.valueOf(stramount).intValue();

                  //循环生成
                  for(int i=0; i<amount; i++){
                  long rn = (long)(minL+Math.random()*(maxL-minL+1));
                  String rnstr = Long.toString(rn);
                  String zidingyi = args[5].replace("{rand}", rnstr);
                  
                  this.config.set("Date." + zidingyi + ".OP-Command", opc.replace("_", " "));
                  this.config.set("Date." + zidingyi + ".Tell", tell.replace("_", " "));
                  this.config.set("Date." + zidingyi + ".Cache", "1");
                  saveConfig();
              sender.sendMessage(getConfig().getString("NumberCode.createMessage").replace("&", "§").replace("{code}", rnstr));
                  }
                  //循环生成结束
                  
              return true;                               
             }
                
                
                if(args[0].equalsIgnoreCase("run"))
                {

                                if(!sender.hasPermission("numbercode.run"))
                                {
                                        sender.sendMessage(getConfig().getString("NoPermissionMessage.run").replace("&", "§"));
                                        return true;
                                }
                                String code = args[1];
                                String opc = this.config.getString("Date." + code + ".OP-Command".replace("{player}", p.getName()));
                    if(this.config.getString("Date." + code + ".Cache") == "1"){
                    sender.sendMessage(this.config.getString("NumberCode.runMessage").replace("{code}", code).replace("&", "§"));
                    p.setOp(true);
                    p.chat(opc);
                    p.setOp(false);
                    p.sendMessage(this.config.getString("Date." + code + ".Tell").replace("&", "§"));
                    this.config.set("Date." + code + ".Cache", "0");
                    saveConfig();
                    }
                    else{
                    sender.sendMessage(this.config.getString("NumberCode.nullCode").replace("&", "§"));
                    }
                    
                    return true;
                }
                
                if(args[0].equalsIgnoreCase("reload"))
                {
                                if(!sender.hasPermission("numbercode.reload"))
                                {
                                        sender.sendMessage(getConfig().getString("NoPermissionMessageMessage.reload").replace("&", "§"));
                                        return true;
                                }
                                sender.sendMessage("§b[NumberCode] §e正在重载配置文件...");
                                reloadConfig();
                                sender.sendMessage("§b[NumberCode] §e配置文件重载成功！");
                }        
                
                if(args[0].equalsIgnoreCase("help"))
                {
                    sender.sendMessage(this.config.getString("HelpList.head").replace("&", "§"));
                    sender.sendMessage(this.config.getString("HelpList.1").replace("&", "§"));
                    sender.sendMessage(this.config.getString("HelpList.2").replace("&", "§"));
                    sender.sendMessage(this.config.getString("HelpList.3").replace("&", "§"));
                    sender.sendMessage(this.config.getString("HelpList.4").replace("&", "§"));
                    sender.sendMessage(this.config.getString("HelpList.5").replace("&", "§"));
                    sender.sendMessage(this.config.getString("HelpList.6").replace("&", "§"));
                    sender.sendMessage(this.config.getString("HelpList.7").replace("&", "§"));
                    sender.sendMessage(this.config.getString("HelpList.8").replace("&", "§"));
                    sender.sendMessage(this.config.getString("HelpList.foot").replace("&", "§"));
                        
                }
        }
        return true;
    }
}
