import org.bukkit.Bukkit;
import org.bukkit.command.Command;
import org.bukkit.command.CommandSender;
import org.bukkit.entity.Player;
import org.bukkit.inventory.ItemStack;

import cn.changshengjue.forge.command.SubCommand;
import cn.changshengjue.forge.command.type.AdminCommand;

public class IfCommand extends SubCommand implements AdminCommand {
    public IfCommand() {
        super("if", "/lore if", "判断手中物品是否有指定lore");
    }

    @Override
    public boolean onCommand(CommandSender sender, Command cmd, String label, String[] args) {
            Player player = Bukkit.getPlayer(sender.getName());
            ItemStack itemInMainHand = player.getInventory().getItemInMainHand();
            if (itemInMainHand.toString().contains("指定字符串")) {
                        //有
                }else {
                        //没有
                }
       return false;
    }
}复制代码
