        public void updateScoreboardContent(Objective objective, Player p) {
                String playerName = target(p);
                Score empty = objective.getScore("");
                Score aaaaaa = objective.getScore("§6当前状态");
                Score Tokens = objective
                                .getScore(ChatColor.GREEN + "硬币: §b" + getPlayersConfig().getInt("Players." + playerName + ".Tokens"));
                Score Death = objective
                                .getScore(ChatColor.GREEN + "死亡数: §b" + getPlayersConfig().getInt("Players." + playerName + ".Death"));
                Score Kills = objective
                                .getScore(ChatColor.GREEN + "杀敌数: §b" + getPlayersConfig().getInt("Players." + playerName + ".Kills"));
                Score 空 = objective.getScore(" ");
                empty.setScore(5);
                aaaaaa.setScore(4);
                Kills.setScore(3);
                Death.setScore(2);
                Tokens.setScore(1);
                空.setScore(0);
                if (this.config.LevelingUpEnabled) {
                        Score jinyanzhi = objective.getScore(ChatColor.GREEN + "经验值: §b" + getExp(p));
                        Score Level = objective.getScore(ChatColor.GREEN + "当前等级：§b");
                        Score Rank = objective.getScore("§b" + getRank(p));
                        Score Exp = objective.getScore(ChatColor.GREEN + "距离下次升级所需经验:");
                        Score 经验值 = objective.getScore(ChatColor.GREEN + "§b" + getNextExp(p));
                        jinyanzhi.setScore(-1);
                        Exp.setScore(-4);
                        经验值.setScore(-5);
                        Level.setScore(-2);
                        Rank.setScore(-3);
                }
        }

