import net.minecraft.entity.player.EntityPlayer;
import net.minecraft.init.MobEffects;
import net.minecraft.item.ItemFood;
import net.minecraft.item.ItemStack;
import net.minecraft.potion.PotionEffect;
import net.minecraft.world.World;

public class ItemWeird extends ItemFood {
    public ItemWeird() {
        // 左起：饥饿值回复、食物饱和度回复、这个食物能不能喂狼
        // 参考：https://minecraft-zh.gamepedia.com/%E9%A5%A5%E9%A5%BF
        super(0, 0F, false);
    }

    // 食物被吃完时调用。
    @Override
    protected void onFoodEaten(ItemStack stack, World worldIn, EntityPlayer player) {
        // 给予玩家生命恢复 III，3 秒（60 刻）
        player.addPotionEffect(new PotionEffect(MobEffects.REGENERATION, 60, 3));

        // 恢复玩家 8.0 生命值（4 颗心）
        player.setHealth(player.getHealth() + 8F);
    }
}复制代码
