  public void SpawnAt(Location loc, String name)
  {
    CraftWorld cw = (CraftWorld)loc.getWorld();
    WorldServer ws = cw.getHandle();
    CraftServer cs = (CraftServer)Bukkit.getServer();
    PlayerInteractManager pim = new PlayerInteractManager(ws);
    EntityPlayer player = new EntityPlayer(cs.getServer(), ws, new GameProfile(UUID.randomUUID(), name), pim);
    player.setPosition(loc.getX(), loc.getY(), loc.getZ());
    player.add(player);
    for (Player p : Bukkit.getOnlinePlayers())
    {
      ((CraftPlayer)p).getHandle().playerConnection.sendPacket(new PacketPlayOutPlayerInfo(PacketPlayOutPlayerInfo.EnumPlayerInfoAction.ADD_PLAYER, new EntityPlayer[] { player }));
      ((CraftPlayer)p).getHandle().playerConnection.sendPacket(new PacketPlayOutNamedEntitySpawn(player));
    }
  }
