        private void skinButton1_Click(object sender, EventArgs e)
        {

            if(!(SearchProc("javaw")))
            {
                MessageBox.Show("请打开我的世界客户端");
                return;
            }
            Process[] proc = Process.GetProcessesByName("javaw");
            IntPtr mc = FindWindow(null, proc[0].MainWindowTitle);
            ShowWindowAsync(mc, 3);
             //自己写自动输入的地方
        }
        public bool SearchProc(string strProcName)
        {
            try
            {
                //精确进程名  用GetProcessesByName  
                Process[] ps = Process.GetProcessesByName(strProcName);
                if (ps.Length > 0)
                {
                    return true;
                }
                else
                {
                    return false;
                }
            }
            catch
            {
                return false;
            }
