package heomcraft.item;

import cpw.mods.fml.common.event.FMLPreInitializationEvent;
import cpw.mods.fml.common.registry.GameRegistry;
import net.minecraft.creativetab.CreativeTabs;
import net.minecraft.item.Item;

public class ItemGoldenEgg extends Item {
        public ItemGoldenEgg() {
                super();
                this.setUnlocalizedName("goldenEgg");
                this.setTextureName("heomcraft:golden_egg");
                this.setCreativeTab(CreativeTabs.tabTools);
        }

        public void preLoad(FMLPreInitializationEvent event) {
                GameRegistry.registerItem(new ItemGoldenEgg(), "golden_egg");
        }
}复制代码

ItemLoader.java
package heomcraft.item;

import cpw.mods.fml.common.event.FMLPreInitializationEvent;
import cpw.mods.fml.common.registry.GameRegistry;
import net.minecraft.item.Item;

public class ItemLoader {
        public static Item goldenEgg = new GoldenEgg();

        public ItemLoader(FMLPreInitializationEvent event) {
                register(goldenEgg, "golden_egg");
        }

        private static void register(Item item, String name) {
                GameRegistry.registerItem(goldenEgg, "golden_egg");
        }
}复制代码
