package be.isach.ultracosmetics.cosmetics.gadgets;

import be.isach.ultracosmetics.UltraCosmetics;
import be.isach.ultracosmetics.util.MathUtils;
import java.util.ArrayList;
import java.util.List;
import java.util.UUID;
import org.bukkit.Bukkit;
import org.bukkit.Location;
import org.bukkit.Material;
import org.bukkit.World;
import org.bukkit.entity.Chicken;
import org.bukkit.entity.Entity;
import org.bukkit.entity.EntityType;
import org.bukkit.entity.Item;
import org.bukkit.entity.Player;
import org.bukkit.event.EventHandler;
import org.bukkit.event.HandlerList;
import org.bukkit.event.entity.EntityUnleashEvent;
import org.bukkit.inventory.ItemStack;
import org.bukkit.plugin.PluginManager;
import org.bukkit.scheduler.BukkitScheduler;
import org.bukkit.util.Vector;

public class GadgetParachute
  extends Gadget
{
  List<Chicken> chickens = new ArrayList();
  boolean active;
  
  public GadgetParachute(UUID owner)
  {
    super(owner, GadgetType.PARACHUTE);
    if (owner != null) {
      Bukkit.getPluginManager().registerEvents(this, UltraCosmetics.getInstance());
    }
  }
  
  void onRightClick()
  {
    Location loc = getPlayer().getLocation();
    
    getPlayer().teleport(loc.clone().add(0.0D, 35.0D, 0.0D));
    
    getPlayer().setVelocity(new Vector(0, 0, 0));
    for (int i = 0; i < 20; i++)
    {
      Chicken chicken = (Chicken)getPlayer().getWorld().spawnEntity(getPlayer().getLocation().add(MathUtils.randomDouble(0.0D, 0.5D), 3.0D, MathUtils.randomDouble(0.0D, 0.5D)), EntityType.CHICKEN);
      this.chickens.add(chicken);
      chicken.setLeashHolder(getPlayer());
    }
    Bukkit.getScheduler().runTaskLaterAsynchronously(UltraCosmetics.getInstance(), new Runnable()
    {
      public void run()
      {
        GadgetParachute.this.active = true;
      }
    }, 5L);
  }
  
  void onLeftClick() {}
  
  private void killParachute()
  {
    for (Chicken chicken : this.chickens)
    {
      chicken.setLeashHolder(null);
      chicken.remove();
    }
    MathUtils.applyVelocity(getPlayer(), new Vector(0.0D, 0.15D, 0.0D));
    this.active = false;
  }
  
  @EventHandler
  public void onLeashBreak(EntityUnleashEvent event)
  {
    if (this.chickens.contains(event.getEntity())) {
      for (Entity ent : event.getEntity().getNearbyEntities(1.0D, 1.0D, 1.0D)) {
        if (((ent instanceof Item)) && (((Item)ent).getItemStack().getType() == Material.LEASH)) {
          ent.remove();
        }
      }
    }
  }
  
  void onUpdate()
  {
    if (this.active)
    {
      if ((!getPlayer().isOnGround()) && (getPlayer().getVelocity().getY() < -0.3D)) {
        MathUtils.applyVelocity(getPlayer(), getPlayer().getVelocity().add(new Vector(0.0D, 0.1D, 0.0D)), true);
      }
      if (getPlayer().isOnGround()) {
        killParachute();
      }
    }
  }
  
  public void onClear()
  {
    killParachute();
    HandlerList.unregisterAll(this);
  }
}
