package net.ME.Try_1;

import net.ME.Try_1.code.Block.BlueBlock;
import net.ME.Try_1.code.item.Bluepieces;
import net.minecraft.block.Block;
import net.minecraft.block.material.Material;
import net.minecraft.client.renderer.block.model.ModelResourceLocation;
import net.minecraft.creativetab.CreativeTabs;
import net.minecraft.item.Item;
import net.minecraft.item.ItemBlock;
import net.minecraftforge.client.model.ModelLoader;
import net.minecraftforge.fml.common.registry.ForgeRegistries;
import net.minecraftforge.fml.relauncher.Side;
import net.minecraftforge.fml.relauncher.SideOnly;

public class ItemLoader_ {
        public static final String modid = "try_1";

        
        
        public static final String BlueBlockId = "blueblock";

        public static BlueBlock blueblock;

        public void lb(Block block,String blockid,CreativeTabs creativetab)
        {
                block.setUnlocalizedName(modid+":"+blockid);
                block.setRegistryName(modid, blockid);
                block.setCreativeTab(creativetab);
                ForgeRegistries.BLOCKS.register(block);
                ForgeRegistries.ITEMS.register(new ItemBlock(block).setRegistryName(modid, blockid));
        }
        public void Load_all_blocks()
        {
                lb(blueblock=new BlueBlock(Material.WATER),BlueBlockId,CreativeTabs.BUILDING_BLOCKS);
        }
        
        
        public static final String Bluepiecesid="bluepieces";
        
        public static Bluepieces bluepieces;
        
        @SideOnly(Side.CLIENT)
        public void li(Item item,String itemid,CreativeTabs creativetab)
        {
                item.setUnlocalizedName(modid+":"+itemid);
                item.setCreativeTab(creativetab);
                ForgeRegistries.ITEMS.register(item.setRegistryName(modid, itemid));
                //ModelResourceLocation model = new ModelResourceLocation(modid+":"+itemid,"inventory");
       // ModelLoader.setCustomModelResourceLocation(item, 0, model);
                ModelLoader.setCustomModelResourceLocation(item, 0, new ModelResourceLocation(modid + ":" + item, "inventory"));

        }
        public void Load_all_items()
        {
                li(bluepieces=new Bluepieces(),Bluepiecesid,CreativeTabs.MATERIALS);
        }
        
        
        
        public ItemLoader_()
        {
                Load_all_blocks();
                Load_all_items();
        }
}




