【MC反作弊开发日记-第二章-通信加密和混淆】第一节-加密和混淆思路讲解第二节-源代码一.git1.请使用git下载最新最全源码,二.混淆工具源码package lona.java.minecraft.pcdtac;

import java.util.Calendar;
import java.util.Random;

public abstract class ObfTool {

    private static final Random random = new Random(Calendar.getInstance().getTimeInMillis());

    /**
     * 混淆某个字节数组
     *
     * @param bytes
     * @return
     */
    public static byte[] obf(byte[] bytes) {
        final byte flag = (byte) random.nextInt();
        byte[] output = new byte[bytes.length + 1];
        output[0] = flag;
        for (int i = 1; i < bytes.length; i++) {
            output[i] = (byte) (bytes[i] ^ flag);
        }
        return output;
    }

    /**
     * 解混淆某个字节数组
     *
     * @param bytes
     * @return
     */
    public static byte[] deobf(byte[] bytes) {
        final byte flag = bytes[0];
        byte[] output = new byte[bytes.length - 1];
        for (int i = 1; i < flag; i++) {
            output[i] = (byte) (bytes[i] ^ flag);
        }
        return output;
    }
}
