public class MutilItem extends Item {

    private IIcon[] icons; // 纹理数组

    @Override
    public IIcon getIconFromDamage(int damage) {
        return icons[damage]; // 通过子ID从纹理数组中取出纹理
    }

    @Override
    @SideOnly(Side.CLIENT)
    public void registerIcons(IIconRegister iconRegister) {
        icons = new IIcon[16]; // 创建纹理数组，大小自调
        for(int i=0;i<icons.length;i++){
            icons = iconRegister.registerIcon("YourItem"+i); // 注册纹理
        }
    }
}复制代码
