execute as @e[tag=C] as @s[tag=random4] run data merge entity @e[type=zombie,limit=1,tag=!used,sort=random] {HandItems:[{id:iron_axe,Count:1b}],Tags:["used"]}
execute as @e[tag=C] as @s[tag=random5] run data merge entity @e[type=zombie,limit=1,tag=!used,sort=random] {HandItems:[{id:iron_shovel,Count:1b}],Tags:["used"]}
execute as @e[tag=C] as @s[tag=random6] run data merge entity @e[type=zombie,limit=1,tag=!used,sort=random] {HandItems:[{id:iron_hoe,Count:1b}],Tags:["used"]}
kill @e[tag=random]复制代码
