源码
package LobbyWorldPro;

import org.bukkit.Bukkit;
import org.bukkit.entity.Player;
import org.bukkit.event.EventHandler;
import org.bukkit.event.Listener;
import org.bukkit.event.block.BlockBreakEvent;
import org.bukkit.event.block.BlockPlaceEvent;
import org.bukkit.event.entity.EntityDamageEvent;
import org.bukkit.event.entity.FoodLevelChangeEvent;
import org.bukkit.event.player.PlayerJoinEvent;
import org.bukkit.event.player.PlayerKickEvent;
import org.bukkit.event.player.PlayerQuitEvent;
import org.bukkit.event.weather.WeatherChangeEvent;
import org.bukkit.plugin.java.JavaPlugin;

import net.minecraft.server.v1_8_R3.World;

public class Main extends JavaPlugin implements Listener
{
  public void onEnable()
  {
    getServer().getPluginManager().registerEvents(this, this);
    Bukkit.getPluginManager().registerEvents(this, this);
        Bukkit.getConsoleSender().sendMessage("§e§l=============================");
        Bukkit.getConsoleSender().sendMessage(" ");
        Bukkit.getConsoleSender().sendMessage(getName() + " §f§l- §a§l插件已启用");
        Bukkit.getConsoleSender().sendMessage("§d§l作者 §f§lClassMate_Potato");
        Bukkit.getConsoleSender().sendMessage(" ");
        Bukkit.getConsoleSender().sendMessage("§e§l=============================");
        saveDefaultConfig();
  }
  
  public void onDisable()
  {
        Bukkit.getConsoleSender().sendMessage("§e§l=============================");
        Bukkit.getConsoleSender().sendMessage(" ");
        Bukkit.getConsoleSender().sendMessage(getName() + " §f§l- §c§l插件已启用");
        Bukkit.getConsoleSender().sendMessage("§d§l作者 §f§lClassMate_Potato");
        Bukkit.getConsoleSender().sendMessage(" ");
        Bukkit.getConsoleSender().sendMessage("§e§l=============================");
        saveDefaultConfig();
  }
  @EventHandler
  public void onPlayerJoin(PlayerJoinEvent e)        //进入信息
  {
    e.setJoinMessage("");
  }
  
  @EventHandler
  public void onPlayerLeft(PlayerQuitEvent e)        //离开信息
  {
    e.setQuitMessage("");
  }
  
  @EventHandler
  public void onPlayerKick(PlayerKickEvent e)        //踢出信息
  {
    e.setLeaveMessage("");
  }
  @EventHandler
  public void onWeatherChange(WeatherChangeEvent e)        //关闭雨
      {
        if (e.toWeatherState())
        {
          e.setCancelled(true);
        }
      }
  @EventHandler
  public void onFoodLevelChangeEvent(FoodLevelChangeEvent e)        //没有饥饿
  {
          Player p = (Player)e.getEntity();
      e.setCancelled(true);
      if (p.getFoodLevel() < 19.0D) 
      {
      p.setFoodLevel(20);
      }
  }
  @EventHandler
  public void onEntityDamage(EntityDamageEvent e)        //掉落伤害
  {
    if (((e.getEntity() instanceof Player)) && (e.getCause() == EntityDamageEvent.DamageCause.FALL)) {
      e.setCancelled(true);
    }
  }
  @EventHandler
  public void onBlockPlace(BlockPlaceEvent e)        //放置方块
    {
      if (!e.getPlayer().hasPermission("LobbyWorldPro.all" + e.getPlayer().getWorld().getName()))
      {
        e.setCancelled(true);
        e.getPlayer().sendMessage(getConfig().getString("NoPermissionMsg"));
      }
    }
  @EventHandler
  public void onBlockBreak(BlockBreakEvent e)        //破坏方块
    {
      if (!e.getPlayer().hasPermission("LobbyWorldPro.all" + e.getPlayer().getWorld().getName()))
      {
        e.setCancelled(true);
        e.getPlayer().sendMessage(getConfig().getString("NoPermissionMsg"));
    }
  }
}复制代码如想自行修改的话,可以反翻译插件后自己改
