package mi.worldgen.ore;

import java.util.Random;

import mi.MI;
import mi.Registry;
import mi.util.exception.NoSuchElementException;

import net.minecraft.block.Block;
import net.minecraft.block.state.IBlockState;
import net.minecraft.util.math.BlockPos;
import net.minecraft.world.World;
import net.minecraft.world.gen.feature.WorldGenMinable;
import net.minecraft.world.gen.feature.WorldGenerator;
import net.minecraftforge.event.terraingen.OreGenEvent;
import net.minecraftforge.event.terraingen.TerrainGen;

public abstract class WorldGeneratorOre extends WorldGenerator
{
    public WorldGeneratorOre(IBlockState state, int[] dataArr){
        if(dataArr.length != 5){
            throw new NoSuchElementException("Need: 5, Current: "+dataArr.length);
        }
        this.state = state;

        this.Ymin = dataArr[0];
        this.Yadd = dataArr[1] - dataArr[0];

        this.veinMin = dataArr[2];
        this.veinAdd = dataArr[3] - dataArr[2];

        this.perChunk = dataArr[4];
    }

    public WorldGeneratorOre(Block block, int[] dataArr){
        if(dataArr.length != 5){
            throw new NoSuchElementException("Need: 5, Current: "+dataArr.length);
        }
        this.state = block.getDefaultState();

        this.Ymin = dataArr[0];
        this.Yadd = dataArr[1] - dataArr[0];

        this.veinMin = dataArr[2];
        this.veinAdd = dataArr[3] - dataArr[2];

        this.perChunk = dataArr[4];
    }

    private final IBlockState state;
    private final int Ymin;
    private final int Yadd;
    private final int veinMin;
    private final int veinAdd;
    private final int perChunk;

    @Override
    public boolean generate(World world, Random rand, BlockPos pos)
    {
        if (TerrainGen.generateOre(world, rand, this, pos, OreGenEvent.GenerateMinable.EventType.CUSTOM))
        {
            MI.logger().debug("[MI/INFO] Oregen("+this.state+")");
            for (int i = 0; i < this.perChunk; ++i)
            {
                int posX = pos.getX() + rand.nextInt(16); // Don't modify!
                int posY = this.Ymin + rand.nextInt(this.Yadd);
                int posZ = pos.getZ() + rand.nextInt(16); // Don't modify!
                BlockPos blockpos = new BlockPos(posX, posY, posZ);
                new WorldGenMinable(this.state, veinMin + rand.nextInt(veinAdd))
                .generate(world, rand, blockpos);
            }
        }
        return true;
    }
}
