var pageTitles=new Array("view=system","type=at","type=friend");
for(var i=0;i<pageTitles.length;i++){
    if(window.location.href.indexOf(pageTitles[i])>-1){
        pageFixed(pageTitles[i]);  
    }
}
function pageFixed(pageTitle){

        var alist=getByClass("pg")[0].getElementsByTagName("a");
        for(var i=0;i<alist.length;i++){alist[i].href+="&"+pageTitle}
        var enter=getByClass("pg")[0].getElementsByTagName("label")[0].getElementsByTagName("input")[0];
        enter.onkeydown=function(){
                if(event.keyCode==13) {
            if(pageTitle=="view=system"){
                window.location='home.php?mod=space&do=notice&view=system&page='+this.value;
            }else if(pageTitle=="type=at"){
                window.location='home.php?mod=space&do=notice&view=mypost&type=at&page='+this.value;
            }else if(pageTitle=="type=friend"){
                window.location='home.php?mod=space&do=notice&view=interactive&type=friend&page='+this.value;
            }
            doane(event);
        }
        
    }
    
}


function getByClass(sClass){
    
                var aResult=[];
                var aEle=document.getElementsByTagName('*');
                for(var i=0;i<aEle.length;i++){
                   if(aEle[i].className==sClass){
                                aResult.push(aEle[i]);
                        }
                }
                return aResult;
};


function var_dump(obj) {
    if(typeof(obj) == "object") {
        var txt = '';
        for(key in obj)
        {
            txt += key + '=' + obj[key] + ',';
        }
        return "Type: "+typeof(obj)+((obj.constructor) ? "nConstructor: "+obj.constructor : "")+"nValue: " + txt;
    } else {
        return "Type: "+typeof(obj)+"nValue: "+obj;
    }
}复制代码
