}
i.pstatus, .quote {
  color: #eeeeee;
}
/* 搜索框和选择框 */
.y_search, .exfm {
  background: none;
  color: #FFF;
}
.y_search_inp, #scbar_txt, .scbar_type_td {
  background: rgba(255,255,255,0.15);
  color: #FFF;
}
.exfm {
  border: 0px;
}
.ftid a, .edt .b2r a.dp {
  background-color: rgba(0,0,0,0.45);
  color: #FFF ! important;
  border: 0px;
}
.px, .pt, .ps, select {
  background: rgba(0,0,0,0.45);
  border: 0px;
  color: #FFF;
}
.tb .a a, .tb .current a {
  background: #555555;
}
.tb a, .ntc_l, #e_controls {
  background: #666666;
}
.edt .bar a {
  border: 1px solid #888888;
}
.edt .bar a:hover {
  background-color: #888888;
  border: 1px solid #888;
}
#e_simple, #e_fullswitcher {
  background: #888888;
  border: 1px solid #888888;
}
.edt .bar, .edt {
  border: 0px;
}
/* 处理按钮 */
#post_extra_tb label, #post_extra_tb label.a, #post_extra_c .exfm {
  background: #333333;
  border: 0px;
}
.pn {
  background: #333;
  border: 0px;
}
.edt .pt, #e_body .area, #e_bbar {
  background: #333333;
  border: 0px;
}
.card_gender_1, .card_gender_2 {
  box-shadow: 0px 0px 8px rgba(0,0,0,0.5);
}
/* 处理粗体字 + 高亮字体问题 */
a[style="font-weight: bold;color: #EE1B2E;background-color: #FFFFFF;"] {
  background-color: rgba(0,0,0,0) ! important;
}
a[style="font-weight: bold;color: #2B65B7;background-color: #FFFF00;"], a[style="font-weight: bold;text-decoration: underline;color: #2B65B7;background-color: #FFFF00;"] {
  background-color: rgba(0,0,0,0) ! important;
  color: #FFFF00 ! important;
}
a[style="font-weight: bold;color: #EE1B2E;"] , a[style="font-weight: bold;color: #EE1B2E;background-color: #FFFFFF;"] {
  color: #EE1B2E ! important;
}
a[style="font-weight: bold;color: #2B65B7;"] {
  color: #2B65B7 ! important;
}
a[style="font-weight: bold;color: #3C9D40;"] {
  color: #3C9D40 ! important;
}
a[style="font-weight: bold;color: #EC1282;"] {
  color: #EC1282 ! important;
}
a[style="font-weight: bold;color: #EE5023;"] {
  color: #EE5023 ! important;
}
a[style="font-weight: bold;color: #8F2A90;"] {
  color: #8F2A90 ! important;
}
/* 处理细体字 + 高亮字体问题 */
a[style="color: #EE1B2E;background-color: #FFFFFF;"] {
  background-color: rgba(0,0,0,0) ! important;
}
a[style="color: #2B65B7;background-color: #FFFF00;"], a[style="text-decoration: underline;color: #2B65B7;background-color: #FFFF00;"] {
  background-color: rgba(0,0,0,0) ! important;
  color: #FFFF00 ! important;
}
a[style="color: #EE1B2E;"] , a[style="color: #EE1B2E;background-color: #FFFFFF;"] {
  color: #EE1B2E ! important;
}
a[style="color: #2B65B7;"] {
  color: #2B65B7 ! important;
}
a[style="color: #3C9D40;"] {
  color: #3C9D40 ! important;
}
a[style="color: #EC1282;"] {
  color: #EC1282 ! important;
}
a[style="color: #EE5023;"] {
  color: #EE5023 ! important;
}
a[style="color: #8F2A90;"] {
  color: #8F2A90 ! important;
}
/* 处理其他 */
.zoom {
  box-shadow: 0px 0px 8px rgba(0,0,0,0.5);
  transition-duration: 0.5s;
}
.zoom:hover {
  box-shadow: 0px 0px 32px rgba(0,0,0,0.5);
}
.new_wp h2 img:hover {
  animation: shadow .5s linear infinite;
  animation: rotate .5s linear infinite;
}
.avt img {
  transition-duration: 1s;
}
.avt img:hover {
  transform: rotate(360deg);
  transition-delay: 0.2s;
  transition-duration: 1s;
  border-radius: 50%;
}
#post_reply img, #post_replytmp img {
  transition-duration: 1s;
}
#post_reply img:hover, #post_replytmp img:hover {
  transition-duration: 1s;
  box-shadow: 0px 0px 16px rgba(0,0,0,0.5);
}
#toptb {
  box-shadow: 0px 0px 16px rgba(0,0,0,0.5);
}
.avtm img {
  border-radius: 50%;
  width: 120px ! important;
  height: 120px ! important;
  transition-duration: 1s;
  box-shadow: 0px 0px 16px rgba(0,0,0,0.5);
}
.avtm img:hover {
  transform: rotate(360deg);
  transition-delay: 0.2s;
  transition-duration: 1s;
  box-shadow: 0px 0px 16px rgba(0,0,0,0.5);
}
.tip {
  background: rgba(0,0,0,0.85);
}
.fl_row td {
  border-color: rgba(255,255,255,0.45);
}
/* 一些 CSS 动画 */
@keyframes shadow {
    0%, 100% {transform: scaleX(1);}
    50% {transform: scaleX(1.2);}
}
@keyframes rotate {
    0% {
    transform: translateY(0) ;
  }
    25% {
        transform: translateY(10px);
    }
    50% {
        transform: translateY(20px) scale(1.1, 0.9);
       
    }
    75% {
        transform: translateY(10px) ;
    }
    100% {
        transform: translateY(0) ;
    }
}
/* 处理 important 优先级问题 */
#portal_block_807 a[style="background-repeat: repeat;;float:;margin-left:px;font-size:13px;color:#333333 !important;;"] {
  color: #FFF ! important;
}复制代码

