            String dirPath = gameDir +
                    "datapacks\\datapack\\data\\datapack\\functions\";
            File folder = new File(dirPath);
            if (!folder.exists()) {
                folder.mkdirs();
                File packFile = new File(gameDir + "datapacks\\datapack\\pack.mcmeta");
                if (!packFile.exists()) {
                    pw = new PrintWriter(packFile);
                    pw.print("{\n  "pack": {\n    "pack_format": 3,\n    "description": "Test"\n  }\n}");
                    pw.flush();
                    pw.close();
                }
            }
            this.pw = new PrintWriter(new FileOutputStream(new File(dirPath + "village.mcfunction")));
        } catch (FileNotFoundException e) {
            e.printStackTrace();
        }
        addVillage();
    }

