        println("setblock ", x + 1, " ", groundY, " ", z + 6, " glowstone replace");
        println("setblock ", x + 1, " ", groundY, " ", z + 11, " glowstone replace");
        println("setblock ", x + 1, " ", groundY, " ", z + 16, " glowstone replace");
        println("setblock ", x + 6, " ", groundY, " ", z + 1, " glowstone replace");
        println("setblock ", x + 6, " ", groundY, " ", z + 6, " glowstone replace");
        println("setblock ", x + 6, " ", groundY, " ", z + 11, " glowstone replace");
        println("setblock ", x + 6, " ", groundY, " ", z + 16, " glowstone replace");
        println("setblock ", x + 11, " ", groundY, " ", z + 1, " glowstone replace");
