    private String makeVillager(Random random) {
        int profession = random.nextInt(5);
        int career = 1;
        switch (profession) {
            case 0:
                career = random.nextInt(4) + 1;
                break;
            case 1:
            case 4:
                career = random.nextInt(2) + 1;
                break;
            case 2:
                career = 1;
                break;
            case 3:
                career = random.nextInt(3) + 1;
                break;
            default:
                break;
        }
        return chain(" {PersistenceRequired:1,Profession:", profession, ",Career:", career, ",CareerLevel:0}");
    }
