protection {
    # 禁止踩踏农田。
    disable-crop-trample {
        mobs=true
        players=true
    }
    mob-griefing {
        # 如果设置为true，将禁止生物破坏地形，白名单内的生物让然可以破坏地形。
        enable-protection=false
        whitelist=[]
    }
}

