package sandtechnology.antispawnerspawn;

import org.bukkit.Bukkit;
import org.bukkit.configuration.file.FileConfiguration;
import org.bukkit.event.EventHandler;
import org.bukkit.event.Listener;
import org.bukkit.event.entity.SpawnerSpawnEvent;
import org.bukkit.plugin.java.JavaPlugin;

public final class AntiSpawnerSpawn extends JavaPlugin implements Listener {

    private FileConfiguration config;

    @Override
    public void onEnable() {
        saveDefaultConfig();
        config=getConfig();
        Bukkit.getServer().getPluginManager().registerEvents(this,this);
        getLogger().info("AntiSpawnerSpawn插件已启用！");
        // Plugin startup logic

    }

    @Override
    public void onDisable() {
        // Plugin shutdown logic
        getLogger().info("AntiSpawnerSpawn插件已禁用！");
    }
    @EventHandler
    public void onSpawnerSpawn(SpawnerSpawnEvent event){
        if(config.getStringList("EnableWorld").contains(event.getEntity().getWorld().getName())){
            event.setCancelled(true);
        }
    }
}
