/**获取状态**/
        public String getStatus(Player p) throws Exception {
                String n = p.getName();
                String constructServiceUrl  = server + server_file_get + "?player=" + n;
            URL url = new URL(constructServiceUrl);
            HttpURLConnection conn = (HttpURLConnection)url.openConnection();
            conn.setConnectTimeout(timeout);
            conn.setRequestMethod("GET");
            InputStream inStream = conn.getInputStream();
            final StringBuilder builder = new StringBuilder(255);
            int byteRead;
            while ((byteRead = inStream.read()) != -1) {
                builder.append((char) byteRead);
            }
            String response = builder.toString();
                return response;
    }

/** 事件监听：玩家加入 **/
        @EventHandler
        public void onPlayerJoin(PlayerJoinEvent e) throws Exception {
                Player p = e.getPlayer();
                String join = config.getString("Messages.Join").replace("&", "§");
                boolean enable = config.getBoolean("Settings.Enable");

                if (enable == true) {
                        Bukkit.getServer().getConsoleSender().sendMessage(getStatus(p));
                        if(getStatus(p) == "Not Logged") {
                                p.sendMessage(prefix + join);
                        } else {
                                String checked = config.getString("Messages.Checked").replace("&", "§");
                                p.sendMessage(prefix + checked);
                        }
                }

