for(var i = 0;i < 360;i++){
    var x = npc.x + Math.sin(i);
    var y = npc.y + 1
    var z = npc.z + Math.cos(i);
    world.spawnParticle('flame',x,y,z,0,0,0,0,0);
}复制代码

