package rain.point;

import org.bukkit.entity.Player;

import me.clip.placeholderapi.expansion.PlaceholderExpansion;

public class Papi extends PlaceholderExpansion{
        private PointCommand pc;
        
        public Papi(PointCommand pc) {
                this.pc = pc;
        }
        
        @Override
    public boolean persist(){
        return true;
    }
        
        @Override
    public boolean canRegister(){
        return true;
    }
        
        @Override
    public String getIdentifier(){
        return "pc";
    }

        @Override
        public String getRequiredPlugin() {
                return "PointCommand";
        }

        @Override
    public String getVersion(){
        return pc.getDescription().getVersion();
    }
        
        @Override
    public String onPlaceholderRequest(Player p, String i){
                if (p==null) return "";
                
                if (i.equals("point")) {
                        return Ma.point.get(p.getName()).toString();
                }
                return null;
        }

        @Override
        public String getAuthor() {
                return pc.getDescription().getAuthors().toString();
        }
}复制代码

