以下是生成这堆文件的 JavaScript 实现：

const fs = require('fs')
const path = require('path')
let page = []
let line = []
for (let i = 0; i <= parseInt('ffff', 16); i++) {
    const char = String.fromCharCode(i)
    if (i !== 0 && i % 16 === 0) {
        page.push(line.join(''))
        line = []
    }
    if (i !== 0 && i % 256 === 0) {
        let codeHex = (i - 1).toString(16)
        if (codeHex.length === 1) {
            codeHex = `000${codeHex}`
        } else if (codeHex.length === 2) {
            codeHex = `00${codeHex}`
        } else if (codeHex.length === 3) {
            codeHex = `0${codeHex}`
        }
        fs.writeFileSync(path.join(__dirname, `unicode_pages/unicode_page_${codeHex.slice(0, 2)}.txt`), page.join('\r\n'), { encoding: 'utf8' })
        page = []
    }
    line.push(char)
}
