package fp;

import ldcr.BridgingAnalyzer.api.BlockSkinProvider;
import ldcr.BridgingAnalyzer.api.BridgingAnalyzerAPI;
import org.bukkit.Material;
import org.bukkit.entity.Player;
import org.bukkit.inventory.ItemStack;

public class Demo implements BlockSkinProvider {
    @Override
    public ItemStack provide(Player player) {
        return new ItemStack(Material.DIAMOND_BLOCK)
    }

    public static void setAPI(){
        BridgingAnalyzerAPI.setBlockSkinProvider(new Demo());
    }
}
