#loader contenttweaker
#ignoreBracketErrors 20


import crafttweaker.block.IBlock;

import crafttweaker.item.IItemStack;
import crafttweaker.item.WeightedItemStack;

import crafttweaker.event.BlockHarvestDropsEvent;
import crafttweaker.events.IEventManager;
import crafttweaker.event.IBlockEvent;

events.onBlockHarvestDrops = function(event){
    var block as string = event.block.definition.id;

    if (block == "immersiveengineering:ore:4"){
        event.drops = [<item:contenttweaker:nickel_rock_ore> % 10,<item:contenttweaker:nickel_rock_ore> % 50];
    }else if(block == "minecraft:emerald_ore"){
        event.drops = [<item:contenttweaker:emerald_rock_ore> % 20];
    }
};







