如题。试过BlockFormEvent 但是完全没用 就是连事件都不触发
版本Paper-209 1.14
我看过其他插件 用的都是BlockFormToEvent 但是看不懂具体怎么操作
能解释一下嘛。。。
这里提供插件源码
package qq2159116373;


import java.io.File;

import org.bukkit.Material;
import org.bukkit.block.Block;
import org.bukkit.event.EventHandler;
import org.bukkit.event.Listener;
import org.bukkit.event.block.BlockFormEvent;
import org.bukkit.plugin.java.JavaPlugin;

public class SK extends JavaPlugin implements Listener {

    @Override
    public void onEnable() {
        super.onEnable();
        
        if(!getDataFolder().exists()) 
        {
        getDataFolder().mkdir();
        }
        File file=new File(getDataFolder(),"config.yml");
        if (!(file.exists())) {saveDefaultConfig();}
        reloadConfig();
        getLogger().info("插件已加载，by mo_java30433");
        getLogger().info("当前版本0.0.1");
        getServer().getPluginManager().registerEvents(this, this);
        if (true == this.getConfig().getBoolean("debug")) {
            getLogger().info("debug状态:开启");
        }

    }

    @Override
    public void onDisable() {
        getLogger().info("插件已关闭，by mo_java30433");
    }

    @EventHandler()
    public void mo(BlockFormEvent e) {
        if (true == this.getConfig().getBoolean("debug")) {
            getLogger().info("成功触发事件");
        }
        Block b = e.getBlock();
        Material bm = b.getType();
        
        if (bm == Material.COBBLESTONE || bm == Material.STONE) {
            
            if (true == this.getConfig().getBoolean("debug")) {
                getLogger().info("成功判断方块");
            }
            
            
            if (e.getBlock().getLocation().getWorld().getName().equalsIgnoreCase("bskyblock_world")) {
                if (true == this.getConfig().getBoolean("debug")) {
                    getLogger().info("正确的世界");
                }
                double r = (double)(1.0+Math.random()*(100.0-1+1));
                //1
                if (r <= 12.0 && r > 0.0) {
                    b.setType(Material.COAL_ORE);
                    return;
                }
                //2
                if (r <= 12.5 && r > 12.0) {
                    b.setType(Material.DIAMOND_ORE);
                    return;
                }
                //3
                if (r <= 13.0 && r > 12.5) {
                    b.setType(Material.EMERALD_ORE);
                    return;
                }
                //4
                if (r <= 15.0 && r > 13.0) {
                    b.setType(Material.GOLD_ORE);
                    return;
                }
                //5
                if (r <= 23.0 && r > 15.0) {
                    b.setType(Material.IRON_ORE);
                    return;
                }
                //6
                if (r <= 24.0 && r > 23.0) {
                    b.setType(Material.LAPIS_ORE);
                    return;
                }
                //7
                if (r <= 25.0 && r > 24.0) {
                    b.setType(Material.REDSTONE_ORE);
                    return;
                }
                //8
                if (r <= 25.1 && r > 25.0) {
                    b.setType(Material.OBSIDIAN);
                    return;
                }
                //end
                if (r <= 100 && r > 25.1) {
                    return;
                }
                
            }
            
            if (e.getBlock().getLocation().getWorld().getName().equalsIgnoreCase("bskyblock_world_nether")) {
                
                if (true == this.getConfig().getBoolean("debug")) {
                    getLogger().info("正确的世界");
                }
                
                double r = (double)(1.0+Math.random()*(100.0-1+1));
                //1
                if (r <= 3.0 && r > 0.0) {
                    b.setType(Material.QUARTZ_ORE);
                    return;
                }
                //2
                if (r <= 6.0 && r > 3.0) {
                    b.setType(Material.GLOWSTONE);
                    return;
                }
                //3
                if (r <= 7.0 && r > 6.0) {
                    b.setType(Material.SOUL_SAND);
                    return;
                }
                //4
                if (r <= 100.0 && r > 7.0) {
                    b.setType(Material.NETHERRACK);
                    return;
                }
                
            }
            
            if (e.getBlock().getLocation().getWorld().getName().equalsIgnoreCase("bskyblock_world_the_end")) {
                
                if (true == this.getConfig().getBoolean("debug")) {
                    getLogger().info("正确的世界");
                }
        
                b.setType(Material.QUARTZ_ORE);
                return;
            }
        }
    }
}


