@EventHandler
// 假设这随便有个什么事件
public void onPlayerXXX(PlayerXXXEvent event) {
    // 获得事件中的玩家
    Player player = event.getPlayer();
    // 获得玩家指着的方块
    Block b = player.getTargetBlock(100);
    // 如果方块的类型是个箱子
    if (b.getType().equals(Material.CHEST)) {
        // 输出消息
        player.sendMessage("你指着一个箱子！");
    }
