function getDistance(x1,y1,z1,x2,y2,z2){
    var x = (x1 - x2) * (x1 - x2);
    var y = (y1 - y2) * (y1 - y2);
    var z = (z1 - z2) * (z1 - z2);
    return Math.sqrt(x + y + z);
}复制代码

