function getDistance(x1,y1,z1,x2,y2,z2,x3,y3,z3){
    var x = (x1 - x2－x3) * (x1 - x2－x3);
    var y = (y1 - y2－y3) * (y1 - y2－y3);
    var z = (z1 - z2－z3) * (z1 - z2－z3);
    return Math.sqrt(x + y + z);
}
