    @SubscribeEvent
    public void onPlayerSwing(PlayerSwingEvent event) {
        if (event.entity instanceof EntityPlayerMP) {
            EntityPlayerMP player = (EntityPlayerMP) event.entity;
            if (WTF.hasWitherPlayer(player)) {
                if (WTF.canAttack(player)) {
                    Vec3 vec=player.getLookVec();
                    EntityWitherSkull skull=new EntityWitherSkull(player.worldObj, player, 0, 0, 0);
                    skull.setPosition(player.posX, player.posY+player.getEyeHeight(), player.posZ);
                    skull.accelerationX=vec.xCoord;
                    skull.accelerationY=vec.yCoord;
                    skull.accelerationZ=vec.zCoord;
                    player.worldObj.spawnEntityInWorld(skull);
                    player.worldObj.playSoundAtEntity(player, "mob.wither.shoot", 1, 1);
                }
            }
        }
    }复制代码


