function tick(e){
    var data = e.player.tempdata;
    if(!e.player.mount){
        data.put('sit_count',0);
        return;
    }
    var count = data.get('sit_count') + 0.5;
    if(count > 4.5){
        count = 0;
        e.player.message('- §7你的状态得到恢复.');
        e.player.setHunger(Math.min(e.player.hunger + 1, 20));
        e.player.setHealth(Math.min(e.player.health + 1, e.player.maxHealth));
    }
    data.put('sit_count',count);
}复制代码
