    public static void main(String[] args) throws IOException, InvalidConfigurationException {
        Gson gson = new Gson();
        Map<String, String> map = gson.fromJson(FileUtils.readFileToString(new File("zh_cn.json"), StandardCharsets.UTF_8), Map.class);
        YamlConfiguration yaml = new YamlConfiguration();
        yaml.load(new File("items.yml"));
        ConfigurationSection section = yaml.getConfigurationSection("Items");
        for (String key : section.getKeys(false)) {
            ConfigurationSection item = section.getConfigurationSection(key);
            String theKey = "block.minecraft." + key.toLowerCase();
            if (map.containsKey(theKey)) {
                item.set("Display Name", map.get(theKey));
                System.out.println(theKey + ":" + item.getString("Display Name"));
            } else {
                theKey = "item.minecraft." + key.toLowerCase();
                item.set("Display Name", map.getOrDefault(theKey, "Missing Key"));
                System.out.println(theKey + ":" + item.getString("Display Name"));
            }
        }
        yaml.save(new File("items2.yml"));
    }复制代码
