package tracker;

import org.bukkit.plugin.java.JavaPlugin;

import java.util.logging.Filter;
import java.util.logging.Logger;

public final class Tracker extends JavaPlugin {

    @Override
    public void onEnable() {
        Logger.getGlobal().setFilter((str)->{
            Filter pre=Logger.getGlobal().getFilter();
            if(str.getMessage().equals("PLAYER")||str.getMessage().equals("Angry Sludge")||str.getMessage().equals("SLIME")){
                getLogger().info("发现目标信息，来源Logger："+str.getLoggerName());
                getLogger().info("辅助诊断的线程信息：");
                Thread.dumpStack();
            }
            if(pre!=null){
                return pre.isLoggable(str);
            }else return true;
        });
        getLogger().info("问答版插件已启用，开始检测异常行为！");
        // Plugin startup logic

    }

    @Override
    public void onDisable() {
        getLogger().info("问答版插件已禁用！");
        // Plugin shutdown logic
    }

}
