                setRegistryName(name);
                setCreativeTab(tab);
                
                BlockInit.BLOCKS.add(this);
                ItemInit.ITEMS.add(new ItemBlock(this).setRegistryName(name));
        }
        
        @Override
        public void registerModels() 
        {
                Main.proxy.registerItemRenderer(Item.getItemFromBlock(this), 0, "inventory");
        }
}
