package idv.jlchntoz;


import ats;
import java.io.BufferedInputStream;
import java.io.File;
import java.io.FileInputStream;
import java.io.InputStream;
import java.net.HttpURLConnection;
import java.net.URL;
import java.util.logging.Level;
import java.util.logging.Logger;
import java.util.regex.Matcher;
import java.util.regex.Pattern;


public class customskinloader
{
  public static final String DefaultSkinURL = "http://skins.minecraft.net/MinecraftSkins/*.png";
  public static final String DefaultCloakURL = "http://skins.minecraft.net/MinecraftCloaks/*.png";
  private static final Pattern skinURLPattern = Pattern.compile("^http://skins.minecraft.net/Minecraft(Skin|Cloak)s/(.*?).png$");
  private static final Logger logger = Logger.getLogger(customskinloader.class.getName());
  private static String[] cloakURLs = null;
  private static String[] skinURLs = null;
  private HttpURLConnection C = null;
  
  public InputStream getPlayerSkinStream(String path)
  {
    Matcher m = skinURLPattern.matcher(path);
    if (m.matches())
    {
      if (m.group(1).contains("Skin")) {
        return getPlayerSkinStream(Boolean.valueOf(false), m.group(2));
      }
      if (m.group(1).contains("Cloak")) {
        return getPlayerSkinStream(Boolean.valueOf(true), m.group(2));
      }
    }
    return getStream(path, Boolean.valueOf(false));
  }
  
  public InputStream getPlayerSkinStream(Boolean isCloak, String playerName)
  {
    if ((skinURLs == null) || (cloakURLs == null) || (skinURLs.length <= 0) || (cloakURLs.length <= 0)) {
      refreshSkinURL();
    }
    for (String l : isCloak.booleanValue() ? cloakURLs : skinURLs)
    {
      String loc = str_replace("*", playerName, l);
      logger.log(Level.INFO, "Try to load " + (isCloak.booleanValue() ? "cloak" : "skin") + " in " + loc);
      
      InputStream S = getStream(loc, Boolean.valueOf(true));
      if (S == null) {
        logger.log(Level.INFO, "No " + (isCloak.booleanValue() ? "cloak" : "skin") + " found in " + loc);
      } else {
        return S;
      }
    }
    logger.log(Level.INFO, "Try to load skin in default URL instead.");
    return getStream(str_replace("*", playerName, isCloak.booleanValue() ? "http://skins.minecraft.net/MinecraftCloaks/*.png" : "http://skins.minecraft.net/MinecraftSkins/*.png"), Boolean.valueOf(true));
  }
  
  private InputStream getStream(String URL, Boolean CheckPNG)
  {
    try
    {
      URL U = new URL(URL);
      this.C = ((HttpURLConnection)U.openConnection());
      this.C.setDoInput(true);
      this.C.setDoOutput(false);
      this.C.connect();
      int respcode = this.C.getResponseCode() / 100;
      if ((respcode != 4) && (respcode != 5))
      {
        BufferedInputStream IS = new BufferedInputStream(this.C.getInputStream());
        if (!CheckPNG.booleanValue()) {
          return IS;
        }
        IS.mark(0);
        byte[] ib = new byte[4];
        IS.read(ib);
        if ((ib[1] == 80) && (ib[2] == 78) && (ib[3] == 71))
        {
          IS.reset();
          return IS;
        }
      }
    }
    catch (Exception ex)
    {
      logger.log(Level.WARNING, ex.getMessage());
    }
    finally
    {
      disconnect();
    }
    return null;
  }
  
  public void disconnect()
  {
    if (this.C != null) {
      this.C.disconnect();
    }
  }
  
  private static void refreshSkinURL()
  {
    try
    {
      File mcdir = ats.w().x;
      skinURLs = readAllLines(mcdir, "skinurls.txt");
      cloakURLs = readAllLines(mcdir, "capeurls.txt");
    }
    catch (Exception ex)
    {
      logger.log(Level.WARNING, ex.getMessage());
    }
    finally
    {
      logger.log(Level.INFO, "Skin URLs Refreshed. Skin count = " + skinURLs.length + ",  Cloak count = " + cloakURLs.length);
    }
  }
  
  private static String[] readAllLines(File mcdir, String path)
  {
    try
    {
      File F = new File(mcdir, path);
      logger.log(Level.INFO, "Config file: " + F.getAbsolutePath());
      if (!F.exists())
      {
        logger.log(Level.INFO, "Config file not found, create new one.");
        F.createNewFile();
        return new String[0];
      }
      if (F.length() <= 0L)
      {
        logger.log(Level.INFO, "Config file is blank, skipped.");
        return new String[0];
      }
      byte[] b = new byte[(int)F.length()];
      BufferedInputStream S = new BufferedInputStream(new FileInputStream(F));
      
      S.read(b);
      S.close();
      logger.log(Level.INFO, "Config file loaded.");
      return str_replace("\r", "\n", str_replace("\r\n", "\n", new String(b))).split("\n");
    }
    catch (Exception ex)
    {
      logger.log(Level.WARNING, ex.getMessage());
    }
    return new String[0];
  }
  
  private static String str_replace(String search, String replace, String subject)
  {
    StringBuffer result = new StringBuffer(subject);
    int pos;
    while ((pos = result.indexOf(search)) != -1) {
      result.replace(pos, pos + search.length(), replace);
    }
    return result.toString();
  }
}


  public static final String DefaultSkinURL = "http://skins.minecraft.net/MinecraftSkins/*.png"; 
 public static final String DefaultCloakURL = "http://skins.minecraft.net/MinecraftCloaks/*.png";
