function init(e){
        e.player.timers.forceStart(1,1200,true);
}
function timer(e){
var world = e.API.getIWorld(0);
var players = world.getAllPlayers();
for(var i = 0 in players){
var camp=players[i].getFactionPoints(6);
var name=players[i].getName();
e.API.executeCommand(world,'scoreboard players set '+name+' renown '+camp );
        }
}复制代码

放到player里边是什么鬼。
给你个计时脚本吧，已经给你设置一分钟了。分别放到运行和更新栏里边
//运行
npc.setTempData(npc.getName(),0);
//更新
time=npc.getTempData(npc.getName())+0.5;
npc.setTempData(npc.getName(),time);
if(time>=60){
//放入你要执行的函数
npc.setTempData(npc.getName(),0);
}复制代码
