#loader contenttweaker
import mods.contenttweaker.VanillaFactory;
import mods.contenttweaker.Item;
import mods.contenttweaker.Commands;

val item = VanillaFactory.createItem("test_item");
item.itemRightClick = function(stack, world, player, hand) {
    Commands.call("gamemode 1", player, world, false, true);  //第一个false为执行命令后不在聊天栏中提示，第二个true为强制执行命令，不管命令执行者有没有权限。
    stack.shrink(1); //消耗1个物品
    return "SUCCESS";
};
item.register();
