#include <graphics.h>
#include <stdlib.h>
#include <stdio.h>
#include <windows.h>

int main() {

        initgraph(640, 480);
        
        setbkcolor(RGB(64, 128, 128));
        cleardevice();
        
        //设置画笔的颜色
        setlinecolor(RGB(255,0,0));
        //设置画笔的颜色,PS_SOLID表示实线, 10表示线的宽度
        setlinestyle(PS_SOLID, 10);
        //设置文字颜色
        settextcolor(RGB(255, 255, 0));
        //设置文字样式 高度 宽度 字体
        SetTextColor(100, 0, "微软雅黑"); //报错了
        //在指定位置输出文本
        char s[] = "无码高清";
        outtextxy(170, 190,s);  //报错了

        //画线 从180,380到460,100
        line(180, 380, 460, 100);
        circle(320, 240, 200);

        system("pause");
        closegraph;

        return 0;
}






