    double sum=0,e=0;
    for(;s<= 4294967294;s=s+2)
    {
         float a=(srand(s)%10000)/10000;
         float b=(srand(s+1)%10000)/10000;
         sum+=abs(a-b);
    }
    e=sum/(s/2);
    printf("%lf",e);
    return 0;
}

