    public static float vSpeed;
    public static float hSpeed;
    public static boolean noclip;

    public Fly(boolean begin, String name) {
        super(begin, name);
        vSpeed = 0.8F;
        hSpeed = 0.8F;
        noclip = false;
    }

    @Override
    public void tick() {
        if (begin && API.isInGame()) {
            EntityPlayer player = API.getPlayer();
            float xm = 0;
            float ym = 0;
            float zm = 0;
            if (Keys.isPressed(API.getMC().gameSettings.keyBindLeft)) {
                xm = hSpeed;
            }
            if (Keys.isPressed(API.getMC().gameSettings.keyBindRight)) {
                xm = -hSpeed;
            }
            if (Keys.isPressed(API.getMC().gameSettings.keyBindJump)) {
                ym = vSpeed;
            }
            if (Keys.isPressed(API.getMC().gameSettings.keyBindSneak)) {
                ym = -vSpeed;
            }
            if (Keys.isPressed(API.getMC().gameSettings.keyBindForward)) {
                zm = hSpeed;
            }
            if (Keys.isPressed(API.getMC().gameSettings.keyBindBack)) {
                zm = -hSpeed;
            }
            float sin = MathHelper.sin(player.rotationYaw * (float) Math.PI / 180.0F);
            float cos = MathHelper.cos(player.rotationYaw * (float) Math.PI / 180.0F);
            player.motionX = xm * cos - zm * sin;
            player.motionY = ym;
            player.motionZ = zm * cos + xm * sin;
            player.noClip = noclip;
        }
    }复制代码
