using namespace std;
#include <windows.h>
#include <dir.h>
#include <direct.h>

void log(string _log)
{
        time_t timep;
        time (&timep);
        char tmp[64];
        strftime(tmp,sizeof(tmp),"%H-%M-%S",localtime(&timep));
        
        ofstream write("RUN_TIME_ERROR.txt",ios::app);
        write<<"["<<tmp<<"][ERROR]"<<_log<<endl;
        write.close();
        return;
}

float get_disk_free_bytes(string v)
{
        unsigned long long int i64FreeBytesToCaller;
        unsigned long long int i64TotalBytes;
        unsigned long long int i64FreeBytes;
        BOOL fResult=GetDiskFreeSpaceEx(v.c_str(),
                (PULARGE_INTEGER)&i64FreeBytesToCaller,
                (PULARGE_INTEGER)&i64TotalBytes,
                (PULARGE_INTEGER)&i64FreeBytes);
        if(fResult)
                return (float)i64FreeBytes;
        return -1;
}

bool is_dir_exists(const string dirName)
{
        DWORD ftyp=GetFileAttributesA(dirName.c_str());
        if(ftyp==INVALID_FILE_ATTRIBUTES)                //路径出错！ 
                return false;
        if(ftyp & FILE_ATTRIBUTE_DIRECTORY)                //文件夹存在 
                return true;
        return false;                                                        //文件夹不存在 
}

bool is_file_exists(const string fileName)
{
        WIN32_FIND_DATA FindFileData;
        return FindFirstFile(fileName.c_str(),&FindFileData)==INVALID_HANDLE_VALUE;//0==存在 
}

bool copyFiles(string srcPath, string desPath)
{
        struct _finddata_t FileInfo;
        string startFindPath=srcPath+"\\*";
        int handle=_findfirst(startFindPath.c_str(),&FileInfo);
        if(!handle)
        {
                cout<<"输入的路径有错误"<<endl;
                return 1;
        }
        string p;
        while(!_findnext(handle,&FileInfo))
                if(FileInfo.attrib==_A_SUBDIR)
                        if(strcmp(FileInfo.name,".") && strcmp(FileInfo.name,".."))
                        {
                                cout<<"subdir:"<<FileInfo.name<<endl;
                                string newPath=srcPath+"\"+FileInfo.name;
                                string newdesPath=desPath+"\"+FileInfo.name;
                                if(_access(newdesPath.c_str(),0)==-1)
                                {
                                        _mkdir(newdesPath.c_str());
                                        copyFiles(newPath, newdesPath);
                                }
                        }
                        else;
                else
                {
                        string srcFilePath=srcPath+"\"+FileInfo.name;
                        string desFilePath=desPath+"\"+FileInfo.name;
                        ifstream in(srcFilePath.c_str(),std::ios::binary);
                        ofstream out(desFilePath.c_str(),std::ios::binary);
                        if(!in)
                        {
                                cout<<"Source file "<<FileInfo.name<<" open file error"<<endl;
                                log("Source file "+(string)FileInfo.name+" open file error");
                                continue;
                        }
                        if(!out)
                        {
                                cout<<"  Dest file  "<<FileInfo.name<<"   create error"<<endl;
                                log("  Dest file  "+(string)FileInfo.name+"   create error");
                        }
                        char flush[2048];
                        while(!in.eof())
                        {
                                in.read(flush,2048);
                                out.write(flush,in.gcount());
                        }
                        cout<<FileInfo.name<<" Copy file succeeds."<<endl;
                        in.close();
                        out.close();
                }
        if(_findclose(handle))
        {
                cout<<"文件句柄关闭失败..."<<endl;
                system("pause");
                return 1;
        }
        return 0;
}

bool copyFile(const char *SourceFile, const char *NewFile)
{
        std::ifstream in;
        std::ofstream out;
        try
        {
                in.open(SourceFile, std::ios::binary);//打开源文件
                if (in.fail())//打开源文件失败
                {
                        std::cout << "Error 1: Fail to open the source file." << std::endl;
                        in.close();
                        out.close();
                        return 0;
                }
                out.open(NewFile, std::ios::binary);//创建目标文件 
                if (out.fail())//创建文件失败
                {
                        std::cout << "Error 2: Fail to create the new file." << std::endl;
                        out.close();
                        in.close();
                        return 0;
                }
                else//复制文件
                {
                        out << in.rdbuf();
                        out.close();
                        in.close();
                        return 1;
                }
        }
        catch (std::exception e)
        {
        }
}
