https://www.bilibili.com/video/av93443139/
pre-release v0.1
完成版本列表雏形的制作，制作图标

pre-release v0.2
增添背景图片，优化图形[attach]1486660[/attach]
alpha 1
B站客服式语言233，一键WIKI主界面代码预览，其它自行下载import java.awt.Color;
import java.awt.*;
import java.awt.event.*;
import java.io.File;
import java.util.Arrays;
import javax.swing.*;
@SuppressWarnings("unchecked")
public class MainGUI {

        @SuppressWarnings("rawtypes")
        public static void main(String[] args) throws Exception {
                // TODO Auto-generated method stub
    System.out.println("Welcome to Friendship_MCL");
    System.out.println("System is now loading, please wait");
    //item define
    Font normalbig = new Font("微软雅黑",Font.BOLD,18);
    Font normaloutput = new Font("宋体",Font.PLAIN,10);
    Color success = new Color(255,80,255);
    Color error = new Color(255,0,0);
    Color lab = new Color(120,160,255);
    String path = "D:/FMCL/";
    //define over
    //versions find
    VersionFinder vfind = new VersionFinder();
    vfind.find();
    int lines = VersionFinder.br();
    String[] versions = VersionFinder.list(lines,1);
    String[] vertype = VersionFinder.list(lines,2);
    String[] verurl = VersionFinder.list(lines,3);
    String[] vertime = VersionFinder.list(lines,4);
    JFrame f = new JFrame("FMCL alpha 1");//创建窗口
    f.setSize(100, 100);
    f.setLocation(0, 0);
    f.setLayout(null);
        f.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
        f.setResizable(false);
        f.setFocusable(true);
    Image icon = (new ImageIcon(path+"assets/texture/icon/titlegui.png")).getImage();
        f.setIconImage(icon);
    System.out.println("Version list:"+Arrays.toString(versions));
    JComboBox vlist = new JComboBox(versions);
    vlist.setBounds(300, 50, 500, 50);
    f.add(vlist);
    JLabel vtip = new JLabel("    先选择一个你钟意的游戏版本鸭~");//提示
    vtip.setBounds(300, 0, 500, 50);
    vtip.setFont(normalbig);
    vtip.setOpaque(true);
    vtip.setBackground(lab);
        f.add(vtip);
        JLabel vinfo = new JLabel("wait a moment");
    vinfo.setBounds(300, 100, 800, 50);
    vinfo.setFont(normaloutput);
        f.add(vinfo);
        JButton wiki = new JButton("版本查询（点此去wiki看看哟~）");
    wiki.setBackground(lab);
    wiki.setBounds(850, 50, 240, 50);
    f.add(wiki);
    wiki.addMouseListener(new MouseAdapter() {//按下查询按钮
        public void mouseClicked(MouseEvent e) {
                WebStation web = new WebStation();
                try {
                                web.open("https://minecraft-zh.gamepedia.com/"+vlist.getSelectedItem().toString());
                        } catch (Exception e1) {
                                // TODO Auto-generated catch block
                                e1.printStackTrace();
                        }
        }
    });
    vtip.setOpaque(true);
    f.setVisible(true);
        JLabel bg = new JLabel();
    int rbg = (int) Math.round(Math.random()*4+.5);
    ImageIcon bgp = new ImageIcon(path+"assets/texture/background/"+rbg+".png");
    bg.setIcon(bgp);
    bg.setBounds(0, 0, bgp.getIconWidth(), bgp.getIconHeight());
    f.add(bg);
    //versions over
    //side bar start
    //side bar over
    f.setSize(1500, 1020);
    Thread.sleep(2);
    f.setSize(1440, 960);
    //always load
    String[] selected = new String[3];
    while(true) {
            try {
            selected = vfind.getinfo(vlist.getSelectedItem().toString(),versions,vertype,verurl,vertime);
            vinfo.setText("大大选中滴版本："+vlist.getSelectedItem().toString()+"，版本类型："+selected[0]);
                vinfo.setForeground(success);
            }catch(NullPointerException npe) {
                    vinfo.setText("选中的内容无效，大大别淘气~");
                    vinfo.setForeground(error);
            }
    }
    //always over
        }

}
