function papiFormat(playerName,text){
  var Class = Java.type("java.lang.Class");
  var Bukkit = Java.type("org.bukkit.Bukkit");
  var PlaceholderAPI = Bukkit.getPluginManager().getPlugin("PlaceholderAPI");
  var classLoader = PlaceholderAPI.class.getClassLoader();
  var find = classLoader.class.getDeclaredMethod("findClass", Class.forName("java.lang.String"));
  find.setAccessible(true);
  var PlaceholderAPI = find.invoke(classLoader, "me.clip.placeholderapi.PlaceholderAPI");
  return PlaceholderAPI.getMethod("setPlaceholders",Class.forName("org.bukkit.OfflinePlayer"),Class.forName("java.lang.String")).invoke(null,Bukkit.getOfflinePlayer(playerName),text);
}复制代码
