package com.github.friendship;

import org.bukkit.Chunk;
import org.bukkit.Location;
import org.bukkit.command.CommandSender;
import org.bukkit.entity.Player;
import org.bukkit.event.Listener;
import org.bukkit.plugin.java.JavaPlugin;
import com.mojang.brigadier.Command;

public class SlimeChunk extends JavaPlugin implements Listener{
        public boolean onCommand(CommandSender sender, @SuppressWarnings("rawtypes") Command cmd, String label, String[] args){
        if (((CommandSender) cmd).getName().equalsIgnoreCase("slime")){          
                Location loc = ((Player)(sender)).getLocation();
            Chunk pl = loc.getChunk();
            int xchunk = pl.getX();
            int zchunk = pl.getZ();
            sender.sendMessage("调试信息：x区块："+xchunk+"，z区块："+zchunk);
            for(int x = xchunk - Integer.valueOf(args[0]) ; x <= xchunk + Integer.valueOf(args[0]);x++) {
                    for(int z = zchunk - Integer.valueOf(args[0]) ; z <= zchunk + Integer.valueOf(args[0]);z++) {
                            if((Math.abs(xchunk-x)+Math.abs(zchunk-z))<=Integer.valueOf(args[0])){
                    //创建区块
                            }
                    }
            }
            return true;                                                       
        }
       return false;
}
}
