var command;

function init(e){
    command = {
        0.1: ['say 10%生命值执行'],
        0.5: ['say 50%生命值执行'],
        0.8: ['say 80%生命值执行'],
        1.0: ['say 100%生命值执行'],
    }
}

function tick(e){
    var apercentage = e.npc.health / e.npc.maxHealth;
    for(var x in command){
        if(apercentage <= x){
            var cmd = command[x];
            delete command[x];
            for(var i in cmd){
                e.npc.executeCommand(cmd[i]);
            }
        }
    }
}复制代码

