底下是匠魂合金附加（TAIGA）里的振金的英勇属性的代码，在游戏里用含有这个属性的匠魂武器打怪会导致游戏崩端，有没有大佬能帮我看看问题出在哪儿了？谢谢
package com.sosnitzka.taiga.traits;

import net.minecraft.entity.EntityLivingBase;
import net.minecraft.item.ItemStack;
import net.minecraft.util.text.TextFormatting;
import slimeknights.tconstruct.library.traits.AbstractTrait;
import slimeknights.tconstruct.library.utils.ToolHelper;

public class TraitHeroic extends AbstractTrait {
  public TraitHeroic() {
    super("heroic", TextFormatting.DARK_GRAY);
  }
  
  public float damage(ItemStack tool, EntityLivingBase player, EntityLivingBase target, float damage, float newDamage, boolean isCritical) {
    float calc;
    int durability = ToolHelper.getCurrentDurability(tool);
    int durabilitymax = ToolHelper.getMaxDurability(tool);
    int safeDenominator = durabilitymax - durability - 1;
    if (safeDenominator <= 0)
      safeDenominator = 1; 
    if (durability * durabilitymax / (durabilitymax - durability - 1 / safeDenominator) != 0) {
      calc = newDamage + newDamage / 2.0F / (durability * durabilitymax / safeDenominator);
    } else {
      calc = newDamage + newDamage / 2.0F / (durability * durabilitymax / safeDenominator + 1);
    } 
    if (durability < (float)(0.1D * durabilitymax) || player.func_110143_aJ() < player.func_110138_aP() / 8.0F || (target.func_110143_aJ() == target.func_110138_aP() && random.nextFloat() > 0.8D))
      return super.damage(tool, player, target, damage, calc, isCritical); 
    return super.damage(tool, player, target, damage, newDamage * 0.9F, isCritical);
  }
}
